import React, { Component } from 'react'
import { INITIAL_FOODLIST, apiKey } from '../../store'
import axios from 'axios'
import DietForm from './DietForm'
// https://api.spoonacular.com/recipes/search?query=cheese&number=2?apiKey=37d5be3315dc4a78acc31544ec2f7a84


export class DietSearcher extends Component {

    state = INITIAL_FOODLIST

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const query = this.state.query
        const endpoint = `https://api.spoonacular.com/food/ingredients/autocomplete?query=${query}&number=10&apiKey=${apiKey}`
        axios.get(endpoint)
            .then(res => {
                const food = res.data
                this.setState({
                    food
                })
                console.log(this.state)
            })
    }

    render() {
        return (
            <div>
                <h1></h1>
                <p>Search and add the food you want and we will tell you if it fits your daily caloric need</p>
                <DietForm handleChange={this.handleChange} handleSubmit={this.handleSubmit} />
            </div>
        )
    }
}

export default DietSearcher
