import React, { Component } from 'react'
import { INITIAL_USERINFO } from '../store'
// import { Grid, FormControlLabel, FormLabel, FormHelperText, InputLabel, Input, InputAdornment, TextField, FormControl, Radio, RadioGroup, Select, MenuItem, Button } from '@material-ui/core'

export class UserForm extends Component {

    state = [ 
        {
            name: null,
            gender: null,
            age: null,
            height: null,
            weight: null,
            activity: 1.2,
            basalMetabolism: null,
            dailyCalories: null
        }
    ]

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })

        const { gender, weight, height, age, activity, basalMetabolism  } = this.state;

        if (gender === 'male') {
            let maleBasalMetabolism = 66.5 + (13.8*weight) + (5*height) - (6.8*age);
            this.setState({
                basalMetabolism: maleBasalMetabolism
            })
        }

        else if (gender === 'female') {
            let femaleBasalMetabolism = 655.1 + (13.8*weight) + (1.8*height) - (4.7*age);
            this.setState({
                basalMetabolism: femaleBasalMetabolism
            })
        }

        this.setState({
            dailyCalories: activity * basalMetabolism
        })
    }

    handleSelect = (e) => {
        this.setState({
            ...this.state,
            [e.target.name]: e.target.value
        })
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state)
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label htmlFor="name">Your name:</label>
                <input type="text" id="name" onChange={this.handleChange} />

                <p>Your gender:</p>
                <label>
                <p>Man</p>
                    <input type="radio" id="gender" name="gender" value="male" onChange={this.handleChange} />
                </label>
                <label>
                    <p>Woman</p>
                    <input type="radio" id="gender" name="gender" value="female" onChange={this.handleChange} />
                </label>
                
                <label htmlFor="height">Your height:</label>
                <input type="number" id="height" onChange={this.handleChange} /> 
        
                <label htmlFor="weight">Your weight:</label>                    
                <input type="number" id="weight" onChange={this.handleChange} />

                <label htmlFor="age">Your age:</label>
                <input type="number" id="age" onChange={this.handleChange} />

                <label htmlFor="activity">Exercise level:</label>
                <select name="activity" id="activity" onChange={this.handleSelect}>
                    <option value={1.2}>Sedentary</option>
                    <option value={1.375}>Light exercises (1-3 days/week)</option>
                    <option value={1.550}>Moderate exercises (3-5 days/week)</option>
                    <option value={1.725}>Heavy exercises (5-7 days/week)</option>
                </select>

                <button type="submit">Show daily calories</button>
            </form>
        )
    }
}

export default UserForm
