import React, { Component } from 'react'
import { INITIAL_MEAL_PLAN } from '../../store'
import axios from 'axios'
import MealPlanResults from './MealPlanResults'
import { apiKey } from '../../store'


export class MealPlan extends Component {

    state = INITIAL_MEAL_PLAN

    showMeals = () => {
        const dailyCalories = JSON.parse(localStorage.getItem('@calorie-police/userInfo'));

        const endpoint = `https://api.spoonacular.com/recipes/mealplans/generate?timeFrame=day&apiKey=${apiKey}`
        axios.get(endpoint)
            .then(res => {
                const meals = res.data;
                this.setState({     
                    food: meals,
                    isMealShown: true
                })
                console.log(this.state)
                console.log(dailyCalories)
            });
    }

    render() {

        return (
            <div>
                <h1>We suggest complete meal plans for your caloric needs!</h1>
                <button onClick={this.showMeals}>Show me!</button>

                { this.state.isMealShown && <MealPlanResults meals={this.state.food.meals} nutrients={this.state.food.nutrients} /> } 
            </div>
        )
    }
}

export default MealPlan
