import React, { Component } from 'react'

export class UserForm extends Component {

    state = {
            name: null,
            gender: null,
            age: null,
            height: null,
            weight: null,
            activity: null,
            metabolismoBasal: null,
        }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        let metabolismoBasal;
        if (this.state.gender === 'male') {
            return  metabolismoBasal = 66.5 + (13.8*this.state.weight) + (5*this.state.height) - (6.8 * this.state.age)
        }
        else if (this.state.gender === 'female') {
            let metabolismoBasal = 655.1 + (13.8*this.state.weight) + (1.8*this.state.height) - (4.7 * this.state.age)
        }
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" placeholder="Opcional" id="" value="" onChange={this.handleChange} /> <br/>
                    <input type="radio" name="gender" id="male" value="male" onChange={this.handleChange} /> Male <br/>
                    <input type="radio" name="gender" id="female" value="female" onChange={this.handleChange}/> Female <br/>
                    <button type="submit">Send</button>
                </form>
                formulário com nome (opcional), sexo, nível de atividade, peso e altura. O app calcula o Valor Energético Total
            </div>
        )
    }
}

export default UserForm
