import React, { Component } from 'react'
import { Grid, FormControlLabel, FormLabel, FormHelperText, InputLabel, Input, InputAdornment, TextField, FormControl, Radio, RadioGroup, Select, MenuItem, Button } from '@material-ui/core'

export class UserForm extends Component {

    state = {
            name: null,
            gender: null,
            age: null,
            height: null,
            weight: null,
            activity: null,
            basalMetabolism: null,
            dailyCalories: null
        }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })

        const { gender, weight, height, age, activity, basalMetabolism } = this.state;

        if (gender === 'male') {
            let maleBasalMetabolism = 66.5 + (13.8*weight) + (5*height) - (6.8*age);
            this.setState({
                basalMetabolism: maleBasalMetabolism
            })
        }

        else if (gender === 'female') {
            let femaleBasalMetabolism = 655.1 + (13.8*weight) + (1.8*height) - (4.7*age);
            this.setState({
                basalMetabolism: femaleBasalMetabolism
            })
        }

        this.setState({
            dailyCalories: activity * basalMetabolism
        })
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        const { name, gender, weight, height, age, activity, basalMetabolism, dailyCalories } = this.state;
        console.log(name, gender, weight, height, age, activity, basalMetabolism, dailyCalories)
    }

    render() {
        return (
                <form onSubmit={this.handleSubmit}>
                    <Grid container spacing={10} alignItems="flex-start">
                        <Grid item xs={3}>
                            <FormControl>
                                <InputLabel htmlFor="name">Your name:</InputLabel>
                                <Input type="text" id="name" onChange={this.handleChange} />
                                <FormHelperText>Optional</FormHelperText>
                            </FormControl>
                        </Grid>
                        
                        <Grid item xs={3}>
                            <FormControl>
                                <FormLabel>Gender: </FormLabel>
                                <RadioGroup aria-label="gender">
                                    <FormControlLabel label="Male" control={<Radio id="gender" value="male" color="primary"/>} onChange={this.handleChange}/>
                                    <FormControlLabel label="Female" control={<Radio id="gender" value="female" color="secondary"/>} onChange={this.handleChange}/>
                                </RadioGroup>
                            </FormControl>
                        </Grid>
                        
                        <Grid item xs={3}>
                            <FormControl>
                                <TextField type="number" id="height" label="Your height" variant="outlined" onChange={this.handleChange} 
                                InputProps={{
                                    endAdornment: <InputAdornment position="end">cm</InputAdornment>,
                                }} />
                    
                                <TextField type="number" id="weight" label="Your weight" variant="outlined" onChange={this.handleChange} 
                                InputProps={{
                                    endAdornment: <InputAdornment position="end">kg</InputAdornment>,
                                }} />

                                <TextField type="number" id="age" label="Your age" variant="outlined" onChange={this.handleChange} 
                                InputProps={{
                                    endAdornment: <InputAdornment position="end">years</InputAdornment>,
                                }} />
                            </FormControl>
                        </Grid>
                        
                        <Grid item xs={3}>
                            <FormControl>
                                <InputLabel htmlFor="activity">Exercise level:</InputLabel>
                                <Select name="activity" id="activity" value={this.state.activity} onChange={this.handleChange}>
                                <MenuItem value={1.2}><em>Sedentary</em></MenuItem>
                                <MenuItem value={1.375}>Light exercises (1-3 days/week)</MenuItem>
                                <MenuItem value={1.550}>Moderate exercises (3-5 days/week)</MenuItem>
                                <MenuItem value={1.725}>Heavy exercises (5-7 days/week)</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>
                        
                        <Grid item xs={10}>
                            <Button type="submit" style={{}} color="primary" variant="contained">Show daily calories</Button>
                        </Grid>  
                    </Grid>  
                </form>
        )
    }
}

export default UserForm
