import React, { Component } from 'react'

export class DietResults extends Component {

    saveRecipe = (meal) => {
        savedRecipes.push(meal.name)
        this.setState(({
            savedRecipes: prevState
        }) )
    }

    render() {
        const { food, saveRecipe } = this.props
        const foodList = food.map(meal => {
        let randomKey = Math.random()*10
        return (
            <div className="foodResultContainer" key={randomKey}>
                <h3>{meal.name}</h3>
                {/* <img src={`https://spoonacular.com/recipeImages/${meal.id}-480x360.jpg`} alt={meal.name} /> */}
                <button onClick={saveRecipe(meal.name)}>Add</button>
            </div>
        )
    })
    return (
        <>
        { foodList }
        </>
    )
    }
}

export default DietResults
