import React, { Component } from 'react'
import { INITIAL_MEAL_PLAN } from '../../store'
import axios from 'axios'
import MealPlanResults from './MealPlanResults'


export class MealPlan extends Component {
//https://api.spoonacular.com/recipes/mealplans/generate?timeFrame=day?apiKey=37d5be3315dc4a78acc31544ec2f7a84

    state = INITIAL_MEAL_PLAN

    showMeals = () => {
        axios.get('https://api.spoonacular.com/recipes/mealplans/generate?timeFrame=day?targetCalories=3000?apiKey=37d5be3315dc4a78acc31544ec2f7a84')
            .then(res => {
                const meals = res.data;
                this.setState({
                    meals: meals,
                    isMealShown: true
                })
            })
    }

    render() {

        return (
            <div>
                <h1>We suggest complete meal plans for your caloric needs!</h1>
                <button onClick={this.showMeals}>Show me!</button>

                { this.state.isMealShown && <MealPlanResults meals={this.state.meals} /> } 
            </div>
        )
    }
}

export default MealPlan
