import React, { Component } from 'react'

export class UserForm extends Component {

    state = {
            name: null,
            gender: null,
            age: null,
            height: null,
            weight: null,
            activity: null,
            metabolismoBasal: null,

        }
    
    handleSubmit = () => {
        this.setState({
            [e.target.value]: this.name
        })

        if (this.state.gender === 'male') {
            let metabolismoBasal = 66.5 + (13.8*this.state.weight) + (5*this.state.height) - (6.8 * this.state.age)
        }
        else if (this.state.gender === 'female') {
            let metabolismoBasal = 655.1 + (13.8*this.state.weight) + (1.8*this.state.height) - (4.7 * this.state.age)
        }
    }

    render() {
        return (
            <div>
                <form onSubmit={() => this.handleSubmit}>
                    <input type="text" placeholder="Opcional" value="" /> <br/>
                    <input type="radio" name="gender" value="male" /> Male <br/>
                    <input type="radio" name="gender" value="female" /> Female <br/>
                    <button type="submit">Send</button>
                </form>
                formulário com nome (opcional), sexo, nível de atividade, peso e altura. O app calcula o Valor Energético Total
            </div>
        )
    }
}

export default UserForm
