import React, { Component } from 'react'
import { FormControlLabel, FormLabel, FormHelperText, InputLabel, Input, InputAdornment, TextField, FormControl, Radio, RadioGroup, Button } from '@material-ui/core'

export class UserForm extends Component {

    state = {
            name: null,
            gender: null,
            age: null,
            height: null,
            weight: null,
            activity: null,
            basalMetabolism: null,
            dailyCalories: null
        }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })

        const { gender, weight, height, age, activity, basalMetabolism } = this.state;

        if (gender === 'male') {
            let maleBasalMetabolism = 66.5 + (13.8*weight) + (5*height) - (6.8*age);
            this.setState({
                basalMetabolism: maleBasalMetabolism
            })
        }

        else if (gender === 'female') {
            let femaleBasalMetabolism = 655.1 + (13.8*weight) + (1.8*height) - (4.7*age);
            this.setState({
                basalMetabolism: femaleBasalMetabolism
            })
        }

        this.setState({
            dailyCalories: activity * basalMetabolism
        })
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        const { name, gender, weight, height, age, activity, basalMetabolism, dailyCalories } = this.state;
        console.log(name, gender, weight, height, age, activity, basalMetabolism, dailyCalories)
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <FormControl>
                        <InputLabel htmlFor="name">Your name:</InputLabel>
                        <Input type="text" id="name" onChange={this.handleChange} />
                        <FormHelperText>Optional</FormHelperText>
                    </FormControl>

                    <FormControl>
                        <FormLabel>Gender: </FormLabel>
                        <RadioGroup aria-label="gender">
                            <FormControlLabel id="gender" label="Male" value="male" control={<Radio color="primary"/>} onChange={this.handleChange}/>
                            <FormControlLabel id="gender" label="Female" value="female" control={<Radio color="secondary"/>} onChange={this.handleChange}/>
                        </RadioGroup>
                    </FormControl>
                   
                    {/* <Radio name="gender" id="gender" value="male" onChange={this.handleChange} /> Male <br/> */}
                    {/* <Radio name="gender" id="gender" value="female" onChange={this.handleChange}/> Female <br/> */}
                    <FormControl>
                        <TextField type="number" id="height" label="Your height" variant="outlined" onChange={this.handleChange} 
                        InputProps={{
                            endAdornment: <InputAdornment position="end">cm</InputAdornment>,
                          }} />
            
                        <TextField type="number" id="weight" label="Your weight" variant="outlined" onChange={this.handleChange} 
                        InputProps={{
                            endAdornment: <InputAdornment position="end">kg</InputAdornment>,
                          }}/>/>

                        <TextField type="number" id="age" label="Your age" variant="outlined" onChange={this.handleChange} />
                        
                    </FormControl>
                    <Button type="submit" color="primary" variant="contained">Show daily calories</Button>
                </form>
            </div>
        )
    }
}

export default UserForm
