import React, { Component } from 'react'

export class UserForm extends Component {

    state = {
            name: null,
            gender: null,
            age: null,
            height: null,
            weight: null,
            activity: null,
            basalMetabolism: null,
            dailyCalories: null
        }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        const { gender, weight, height, age, activity, basalMetabolism, dailyCalories } = this.state;

        if (gender === 'male') {
            this.setState({
                basalMetabolism: 66.5 + (13.8*weight) + (5*height) - (6.8*age)
            })
        }
        else if (gender === 'female') {
            this.setState({
                basalMetabolism: 655.1 + (13.8*weight) + (1.8*height) - (4.7*age)
            })
        }

         this.setState({
            dailyCalories: activity * basalMetabolism
        })

        console.log(gender,dailyCalories)
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" placeholder="Opcional" id="" value="" onChange={this.handleChange} /> <br/>
                    <input type="radio" name="gender" id="male" value="male" onChange={this.handleChange} /> Male <br/>
                    <input type="radio" name="gender" id="female" value="female" onChange={this.handleChange}/> Female <br/>
                    <button type="submit">Send</button>
                </form>
                formulário com nome (opcional), sexo, nível de atividade, peso e altura. O app calcula o Valor Energético Total
            </div>
        )
    }
}

export default UserForm
