import React from 'react'

const UserRecipes = ({savedRecipes, isSavedRecipeShown}) => {
    const savedRecipesList = savedRecipes.map((recipeArray, index) => {
        const recipe = recipeArray.newRecipe.map(recipe => {
            return recipe.title
        })
        let randomKey = Math.random()*10
        return (
            <div key={randomKey}>
                <img src={`https://spoonacular.com/recipeImages/${recipe.newRecipe.id}-240x150.jpg`} alt={recipe.newRecipe.title} />
                <h3>{ recipe.newRecipe.title }</h3>
                {/* <p></p> */}
            </div>
        )
    })

    // const recipesInfo = savedRecipes[]

    return (
        <>
            <h2>Your recipes</h2>
            { isSavedRecipeShown ? savedRecipesList : <p>There are no recipes saved yet</p> }
        </>
    )
}

export default UserRecipes
