import React, { Component } from 'react'
import { INITIAL_MEAL_PLAN } from '../../store'
import axios from 'axios'
import MealPlanResults from './MealPlanResults'
import { apiKey } from '../../store'


export class MealPlan extends Component {

    state = INITIAL_MEAL_PLAN

    showMeals = () => {
        axios.get(`https://api.spoonacular.com/recipes/mealplans/generate?timeFrame=day&apiKey=${apiKey}`)
            .then(res => {
                const meals = res.data;
                this.setState({
                    meals,
                    isMealShown: true
                })
            });
        
        console.log(this.state)
    }

    render() {

        return (
            <div>
                <h1>We suggest complete meal plans for your caloric needs!</h1>
                <button onClick={this.showMeals}>Show me!</button>

                {/* { this.state.isMealShown && <MealPlanResults meals={this.state.meals} /> }  */}
            </div>
        )
    }
}

export default MealPlan
