import React, { useState } from 'react'
import { INITIAL_USERINFO } from '../store'

export default function UserForm2({ INITIAL_USERINFO }) {

    const [userInfo, setUserInfo] = useState(INITIAL_USERINFO)

    function handleChange(e) {
        setUserInfo({
            [e.target.id]: e.target.value
        })

        const { gender, weight, height, age, activity, basalMetabolism  } = this.state;

        if (gender === 'male') {
            let maleBasalMetabolism = 66.5 + (13.8*weight) + (5*height) - (6.8*age);
            setUserInfo({
                basalMetabolism: maleBasalMetabolism
            })
        }

        else if (gender === 'female') {
            let femaleBasalMetabolism = 655.1 + (13.8*weight) + (1.8*height) - (4.7*age);
            setUserInfo({
                basalMetabolism: femaleBasalMetabolism
            })
        }

        setUserInfo({
            dailyCalories: activity * basalMetabolism
        })
    }

    function handleSelect(e) {
        setUserInfo({
            ...userInfo,
            [e.target.name]: e.target.value
        })
    }
    
    function handleSubmit(e) {
        e.preventDefault();
        console.log(userInfo)
    }

        return (
                <form onSubmit={handleSubmit}>
                                <label htmlFor="name">Your name:</label>
                                <input type="text" id="name" onChange={handleChange} />

                                <label htmlFor="gender">Your gender: </label>
                                    <input type="radio" id="gender" value="male" color="primary" onChange={handleChange} />
                                    <radio id="gender" value="female" color="secondary" onChange={handleChange} />

                                <label htmlFor="height">Your height:</label>
                                <input type="number" id="height" onChange={handleChange} /> 
                         
                    
                                <input type="number" id="weight" label="Your weight" onChange={handleChange} />

                                <input type="number" id="age" onChange={handleChange} />
        
                                <label htmlFor="activity">Exercise level:</label>
                                <select name="activity" id="activity" onChange={handleSelect}>
                                    <option value={1.2}>Sedentary</option>
                                    <option value={1.375}>Light exercises (1-3 days/week)</option>
                                    <option value={1.550}>Moderate exercises (3-5 days/week)</option>
                                    <option value={1.725}>Heavy exercises (5-7 days/week)</option>
                                </select>

                            <button type="submit" color="primary" variant="contained">Show daily calories</button>
            </form>
        )
    }
