import React, { useState } from 'react'
import { Grid, FormControlLabel, FormLabel, FormHelperText, InputLabel, Input, InputAdornment, TextField, FormControl, Radio, RadioGroup, Select, MenuItem, Button } from '@material-ui/core'

export default function UserForm() {

    const [userInfo, setUserInfo] = useState([
        {
            name: null,
            gender: null,
            age: null,
            height: null,
            weight: null,
            activity: 1.2,
            basalMetabolism: null,
            dailyCalories: null
        }
    ]);

    function handleChange(e) {
        setUserInfo({
            ...userInfo,
            [e.target.id]: e.target.value
        })

        let gender, weight, height, age, activity, dailyCalories, basalMetabolism 

        if (gender === 'male') {
            let maleBasalMetabolism = 66.5 + (13.8*weight) + (5*height) - (6.8*age);
            setUserInfo([
                basalMetabolism = maleBasalMetabolism
            ])
        }

        else if (gender === 'female') {
            let femaleBasalMetabolism = 655.1 + (13.8*weight) + (1.8*height) - (4.7*age);
            setUserInfo([
                basalMetabolism = femaleBasalMetabolism
            ])
        }

        setUserInfo([
            dailyCalories = activity * basalMetabolism
        ])
    }
    
    function handleSubmit(e) {
        e.preventDefault();
        const { name, gender, weight, height, age, activity, basalMetabolism, dailyCalories } = thsuserInfo;
        console.log(name, gender, weight, height, age, activity, basalMetabolism, dailyCalories)
    }

        return (
                <form onSubmit={handleSubmit}>
                    <Grid container spacing={10} alignItems="flex-start">
                        <Grid item xs={3} dm={8}>
                            <FormControl>
                                <InputLabel htmlFor="name">Your name:</InputLabel>
                                <Input type="text" id="name" onChange={handleChange} />
                                <FormHelperText>Optional</FormHelperText>
                            </FormControl>
                        </Grid>
                        
                        <Grid item xs={3} dm={8}>
                            <FormControl>
                                <FormLabel>Gender: </FormLabel>
                                <RadioGroup aria-label="gender">
                                    <FormControlLabel label="Male" control={<Radio id="gender" value="male" color="primary"/>} onChange={handleChange}/>
                                    <FormControlLabel label="Female" control={<Radio id="gender" value="female" color="secondary"/>} onChange={handleChange}/>
                                </RadioGroup>
                            </FormControl>
                        </Grid>
                        
                        <Grid item xs={3} dm={8}>
                            <FormControl>
                                <TextField type="number" id="height" label="Your height" variant="outlined" onChange={handleChange} 
                                InputProps={{
                                    endAdornment: <InputAdornment position="end">cm</InputAdornment>,
                                }} />
                    
                                <TextField type="number" id="weight" label="Your weight" variant="outlined" onChange={handleChange} 
                                InputProps={{
                                    endAdornment: <InputAdornment position="end">kg</InputAdornment>,
                                }} />

                                <TextField type="number" id="age" label="Your age" variant="outlined" onChange={handleChange} 
                                InputProps={{
                                    endAdornment: <InputAdornment position="end">years</InputAdornment>,
                                }} />
                            </FormControl>
                        </Grid>
                        
                        <Grid item xs={3} dm={8}>
                            <FormControl>
                                <InputLabel htmlFor="activity">Exercise level:</InputLabel>
                                <Select name="activity" id="activity" onChange={handleChange}>
                                    <MenuItem value={1.2}>Sedentary</MenuItem>
                                    <MenuItem value={1.375}>Light exercises (1-3 days/week)</MenuItem>
                                    <MenuItem value={1.550}>Moderate exercises (3-5 days/week)</MenuItem>
                                    <MenuItem value={1.725}>Heavy exercises (5-7 days/week)</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>
                        
                        <Grid item xs={10} dm={10}>
                            <Button type="submit" color="primary" variant="contained">Show daily calories</Button>
                        </Grid>  
                    </Grid>  
                </form>
        )
    }
