import React, { Component } from 'react'

export class UserForm extends Component {

    state = {
            name: null,
            gender: null,
            age: null,
            height: null,
            weight: null,
            activity: null,
            basalMetabolism: Number,
            dailyCalories: null
        }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })

        const { name, gender, weight, height, age, activity, basalMetabolism, dailyCalories } = this.state;

        if (gender === 'male') {
            let maleBasalMetabolism = 66.5 + (13.8*weight) + (5*height) - (6.8*age);
            this.setState({
                basalMetabolism: maleBasalMetabolism
            })
        }

        else if (gender === 'female') {
            let femaleBasalMetabolism = 655.1 + (13.8*weight) + (1.8*height) - (4.7*age);
            this.setState({
                basalMetabolism: femaleBasalMetabolism
            })
        }
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        const { name, gender, weight, height, age, activity, basalMetabolism, dailyCalories } = this.state;

        // if (gender === 'male') {
        //     let maleBasalMetabolism = 66.5 + (13.8*weight) + (5*height) - (6.8*age);
        //     this.setState({
        //         basalMetabolism: maleBasalMetabolism
        //     })
        // }

        // else if (gender === 'female') {
        //     let femaleBasalMetabolism = 655.1 + (13.8*weight) + (1.8*height) - (4.7*age);
        //     this.setState({
        //         basalMetabolism: femaleBasalMetabolism
        //     })
        // }

      

        console.log(name, gender, weight, height, age, activity, basalMetabolism, dailyCalories)
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" id="name" onChange={this.handleChange} /> <br/>
                    <input type="radio" name="gender" id="gender" value="male" onChange={this.handleChange} /> Male <br/>
                    <input type="radio" name="gender" id="gender" value="female" onChange={this.handleChange}/> Female <br/>
                    <input type="number" id="height" onChange={this.handleChange}></input>
                    <input type="number" id="weight" onChange={this.handleChange}></input>
                    <input type="number" id="age" onChange={this.handleChange}></input>
                    <button type="submit">Send</button>
                </form>
            </div>
        )
    }
}

export default UserForm
