import React from 'react'

const UserRecipes = ({savedRecipes, isSavedRecipeShown}) => {
    const savedRecipesList = savedRecipes.map(recipe => {
        return (
            <div key={recipe.id}>
                
                <img src={`https://spoonacular.com/recipeImages/${meal.id}-480x360.jpg`} alt={meal.title} />
                <h3>{ recipe.title }</h3>
                {/* <p></p> */}
            </div>
        )
    })

    return (
        <>
            <h2>Your recipes</h2>
            { isSavedRecipeShown ? savedRecipesList : <p>There are no recipes saved yet</p> }
        </>
    )
}

export default UserRecipes
