            height: null,
            weight: null,
            activity: null,
        }
    ]

    //TMB Mulher= 655,1 + (9,5 x Peso (kg)) + (1,8 x Altura (cm)) – (4,7 x Idade)
    //TMB Homem= 66,5 + (13,8 x Peso (kg)) + (5 x Altura (cm)) – (6,8 x Idade)
    
   function 

    render() {
        return (
            <div>
                <form action="">
                    <input type="text" placeholder="Opcional" value="" /> <br/>
                    <input type="radio" name="gender" value="male" /> Male <br/>
                    <input type="radio" name="gender" value="female" /> Female <br/>
                    <button type="submit">Send</button>
                </form>
                formulário com nome (opcional), sexo, nível de atividade, peso e altura. O app calcula o Valor Energético Total
            </div>
        )
    }
}

export default UserForm
